# Last changed: 2010-08-16

__all__ = ["VertexFunction", "EdgeFunction", "FaceFunction", "FacetFunction", "CellFunction"]

from dolfin.cpp import MeshFunction

def VertexFunction(value_type, mesh):
    "Create MeshFunction of topological dimension 0 on given mesh."
    return MeshFunction(value_type, mesh, 0)

def EdgeFunction(value_type, mesh):
    "Create MeshFunction of topological dimension 1 on given mesh."
    return MeshFunction(value_type, mesh, 1)

def FaceFunction(value_type, mesh):
    "Create MeshFunction of topological dimension 2 on given mesh."
    return MeshFunction(value_type, mesh, 2)

def FacetFunction(value_type, mesh):
    "Create MeshFunction of topological codimension 1 on given mesh."
    return MeshFunction(value_type, mesh, mesh.topology.dim() - 1)

def CellFunction(value_type, mesh):
    "Create MeshFunction of topological codimension 0 on given mesh."
    return MeshFunction(value_type, mesh, mesh.topology().dim())
